using Syncfusion.UI.Xaml.BulletGraph;
using Windows.UI;
using Windows.UI.Xaml.Controls;
using Windows.UI.Xaml.Media;
namespace BulletGraphSample
{
    public sealed partial class MainPage : Page
    {
        public MainPage()
        {
            this.InitializeComponent();
            SfBulletGraph bulletGraph = new SfBulletGraph();
            bulletGraph.FlowDirection = BulletGraphFlowDirection.Backward;
            bulletGraph.QualitativeRanges = new QualitativeRangeCollection();
            bulletGraph.QualitativeRanges.Add(new QualitativeRange { RangeEnd =
30, RangeOpacity = 0.5, RangeStroke = new SolidColorBrush(Colors.Red) });
            bulletGraph.QualitativeRanges.Add(new QualitativeRange { RangeEnd =
60, RangeOpacity = 0.5, RangeStroke = new SolidColorBrush(Colors.Green) });
            bulletGraph.QualitativeRanges.Add(new QualitativeRange { RangeEnd =
100, RangeOpacity = 0.5, RangeStroke = new SolidColorBrush(Colors.Blue) });
            Layout.Children.Add(bulletGraph);
        }
    }
}

